export const createBooking = (newBooking) => async (dispatch, getState) => {
  dispatch({
    type: BOOKING_REGISTRATION_DATA_REQUEST,
    payload: newBooking,
  });
  try {
    const {
      userSignin: { userInfo },
    } = getState();

    const { data } = await Axios.post("/api/booking", newBooking, {
      headers: { Authorization: `Bearer ${userInfo.token}` },
    });
    dispatch({
      type: BOOKING_REGISTRATION_DATA_SUCCESS,
      payload: data.booking,
    });
  } catch (error) {
    dispatch({
      type: BOOKING_REGISTRATION_DATA_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
