import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createBooking } from "../actions/BookingAction";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { BOOKING_REGISTRATION_DATA_RESET } from "../constants/BookingConstants";

function BookingButton(props) {
  const dispatch = useDispatch();
  useEffect(() => {
    if (success) {
      dispatch({ type: BOOKING_REGISTRATION_DATA_RESET });
      props.history.push(`/booking/${booking._id}/edit`);
    }
  }, [success, props.history, dispatch, booking]);
  const bookingCreate = useSelector((state) => state.bookingCreate);
  const { loading, success, error, booking } = bookingCreate;

  const createhandler = () => {
    dispatch(createBooking());
  };

  return (
    <div>
      <button type="button" onClick={createhandler}>
        Book now
      </button>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
    </div>
  );
}

export default BookingButton;
