export const detailsBooking = (bookingId) => async (dispatch) => {
  dispatch({ type: BOOKING_REGISTRATION_DATA_REQUEST, payload: bookingId });
  try {
    const { data } = await Axios.get(`/api/products/${bookingId}`);
    dispatch({ type: BOOKING_REGISTRATION_DATA_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: BOOKING_REGISTRATION_DATA_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

